(function(){document.addEventListener("DOMContentLoaded",function(){var gallery=document.querySelector(".product-gallery .woocommerce-product-gallery__image");if(!gallery)return;var img=gallery.querySelector("img");if(!img)return;gallery.classList.add("zoom-follow");var scale=1.6;var isZooming=false;function onMouseMove(e){var rect=gallery.getBoundingClientRect();var imgRect=img.getBoundingClientRect();var x=(e.clientX-imgRect.left)/imgRect.width;var y=(e.clientY-imgRect.top)/imgRect.height;x=Math.max(0,Math.min(1,x));y=Math.max(0,Math.min(1,y));var tx=(x-.5)*(imgRect.width*(scale-1));var ty=(y-.5)*(imgRect.height*(scale-1));img.style.transform="scale("+scale+") translate("+-tx/scale+"px,"+-ty/scale+"px)";img.classList.add("is-zooming");gallery.classList.add("zooming");isZooming=true}function onMouseLeave(){img.style.transform="";img.classList.remove("is-zooming");gallery.classList.remove("zooming");isZooming=false}gallery.addEventListener("mousemove",onMouseMove);gallery.addEventListener("mouseleave",onMouseLeave)})})();